/* ===================== VARIABLES ===================== */
:root {
  --primary-color: #6A0DAD;
  --secondary-color: #9B30FF;
  --background-color: #EDE7F6;
  --text-color: #333333;
  --subtext-color: #555555;
  --button-hover: #B347FF;
  --shadow-color: rgba(0,0,0,0.1);
  --shadow-hover: rgba(0,0,0,0.15);
  --hero-gradient: linear-gradient(-45deg, var(--primary-color), var(--secondary-color), #FF7F50, #FFB347);
}

/* ===================== GLOBAL ===================== */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
body {
  font-family: 'Poppins', sans-serif;
  line-height: 1.6;
  color: var(--text-color);
  scroll-behavior: smooth;
  background: var(--background-color);
}
section {
  padding: 100px 10% 80px 10%; /* Extra top padding to avoid navbar overlap */
  text-align: center;
}

/* ===================== HEADINGS ===================== */
h2 {
  font-size: 2.5rem;
  margin-bottom: 1rem;
  color: var(--primary-color);
}
h3 {
  color: var(--primary-color);
  font-size: 1.5rem;
  margin-bottom: 1rem;
}

/* ===================== NAVBAR ===================== */
.navbar {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 5%;
  position: fixed;
  width: 100%;
  top: 0;
  z-index: 1000;
  transition: all 0.3s ease;
  background: transparent;
}
.navbar.scrolled {
  background: var(--background-color);
  box-shadow: 0 3px 10px var(--shadow-color);
}
.logo {
  font-weight: 700;
  font-size: 1.7rem;
}
.logo .next {
  color: var(--primary-color);
}
.logo .edge {
  color: var(--secondary-color);
}
.nav-links {
  display: flex;
  gap: 2rem;
}
.nav-links a {
  text-decoration: none;
  color: var(--text-color); /* Fix: visible on light bg */
  font-weight: 500;
  position: relative;
  transition: color 0.3s ease;
}
.navbar.scrolled .nav-links a {
  color: var(--text-color);
}
.nav-links a::after {
  content: "";
  position: absolute;
  left: 0;
  bottom: -5px;
  width: 0;
  height: 2px;
  background: var(--primary-color);
  transition: width 0.3s ease;
}
.nav-links a:hover::after,
.nav-links a.active::after {
  width: 100%;
}
.nav-links a.active {
  color: var(--primary-color);
  font-weight: 600;
}
/* CTA button in navbar */
.nav-links .quote-btn {
  background: #FF6B35;   /* or your primary color */
  color: #fff !important;
  padding: 0.6rem 1.2rem;
  border-radius: 30px;
  font-weight: 600;
  transition: all 0.3s ease; 
}

.nav-links .quote-btn:hover {
  background: #e85c2b;  /* darker hover shade */
  transform: translateY(-20px);
}


/* Hamburger */
.hamburger {
  display: none;
  flex-direction: column;
  cursor: pointer;
  gap: 5px;
}
.hamburger span {
  width: 25px;
  height: 3px;
  background: var(--primary-color);
  border-radius: 3px;
  transition: all 0.3s ease;
}

/* ===================== HERO ===================== */
.hero {
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  position: relative;
  overflow: hidden;
  color: #222;
}
.hero::before {
  content: "";
  position: absolute;
  top: 0; left: 0; right: 0; bottom: 0;
  background: var(--hero-gradient);
  background-size: 400% 400%;
  animation: gradientMove 12s ease infinite;
  z-index: -2;
}
@keyframes gradientMove {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}
#heroCanvas {
  position: absolute;
  top: 0; left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
}
.hero-content {
  background: var(--background-color);
  padding: 2rem 3rem;
  border-radius: 16px;
  border-left: 6px solid var(--primary-color);
  box-shadow: 0 10px 35px rgba(0,0,0,0.2);
  display: inline-block;
  text-align: center;
}
.hero-content h2 {
  color: #444;
  margin-top: 0.5rem;
}
.hero-content p {
  font-size: 1.2rem;
  max-width: 600px;
  margin: 1rem auto 2rem auto;
}
.btn {
  padding: 0.75rem 1.5rem;
  background: var(--primary-color);
  color: var(--background-color);
  border-radius: 25px;
  font-weight: 600;
  text-decoration: none;
  transition: all 0.3s ease;
}
.btn:hover {
  background: var(--button-hover);
  color: var(--background-color);
}

/* ===================== ABOUT ===================== */
#about {
  position: relative;
  overflow: hidden;
  border-radius: 16px;
  padding: 100px 10%;
}
#about h2, #about p {
  z-index: 2;
  position: relative;
}
#aboutCanvas {
  position: absolute;
  top: 0; left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
}
/* Background Quote in About Section */
#about {
  position: relative;
  overflow: hidden;
}

.about-quote {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  font-size: 3rem;
  font-weight: 700;
  color: rgba(0, 0, 0, 0.05); /* subtle transparent text */
  white-space: nowrap;
  pointer-events: none;
  z-index: 0;
  text-align: center;
  animation: floatQuote 6s ease-in-out infinite alternate;
  background: linear-gradient(90deg, var(--primary-color), var(-- secondary-color));
   webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  opacity: 0.08; /* keep it subtle */
}


@keyframes floatQuote {
  0% { transform: translate(-50%, -52%); opacity: 0.7; }
  100% { transform: translate(-50%, -48%); opacity: 1; }
}

.about-content {
  position: relative;
  z-index: 1;
  max-width: 800px;
  margin: 0 auto;
  text-align: center;
}


/* ===================== SERVICES ===================== */
.services-section {
  padding: 100px 10%;
  text-align: center;
  background: #fef8f6;
}
.services-section h2 {
  margin-bottom: 0.5rem;
}
.services-section p {
  color: var(--subtext-color);
  font-size: 1.2rem;
  margin-bottom: 3rem;
}
.services-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 30px;
  justify-items: center;
}
.service-card {
  background: var(--background-color);
  padding: 2rem 1.5rem;
  border-radius: 16px;
  box-shadow: 0 10px 30px var(--shadow-color);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  text-align: left;
  width: 100%;
  max-width: 350px;
}
.service-card:hover {
  transform: translateY(-8px);
  box-shadow: 0 15px 35px var(--shadow-hover);
}
.service-card h3 {
  margin-bottom: 1rem;
}
.service-card ul {
  list-style: none;
  padding-left: 0;
  margin-bottom: 1.5rem;
}
.service-card ul li {
  position: relative;
  padding-left: 25px;
  margin-bottom: 0.8rem;
  color: var(--text-color);
}
.service-card ul li::before {
  content: "✓";
  position: absolute;
  left: 0;
  color: var(--primary-color);
  font-weight: bold;
}
.service-card .btn {
  display: inline-block;
  text-decoration: none;
  padding: 0.65rem 1.5rem;
  border-radius: 25px;
  background: var(--primary-color);
  color: var(--background-color);
  font-weight: 600;
  transition: all 0.3s ease;
}
.service-card .btn:hover {
  background: var(--button-hover);
}
.service-card.new-web { border-top: 4px solid var(--primary-color); }
.service-card.upgrade-web { border-top: 4px solid var(--secondary-color); }

/* ===================== CONTACT ===================== */
#contact {
  position: relative;
  border-radius: 16px;
  padding: 100px 10% 80px 10%;
  overflow: hidden;
  text-align: center;
}
#contact h2, #contact p {
  position: relative;
  z-index: 2;
  color: var(--text-color);
}
#contactCanvas {
  position: absolute;
  top: 0; left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
}

/* Contact services */
.contact-services {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 20px;
  margin: 2rem 0;
}
.service-item {
  background: var(--background-color);
  padding: 1rem;
  border-radius: 12px;
  box-shadow: 0 5px 20px var(--shadow-color);
  font-weight: 500;
  display: flex;
  align-items: center;
  gap: 10px;
  transition: transform 0.3s ease;
}
.service-item:hover {
  transform: translateY(-5px);
}

/* Stats */
.contact-stats {
  display: flex;
  justify-content: center;
  gap: 30px;
  margin: 2rem 0;
  font-size: 1.2rem;
}
.contact-stats div strong {
  display: block;
  font-size: 1.5rem;
  color: var(--primary-color);
}

/* Contact CTA and Info Spacing */
#contact .btn {
  margin: 2rem 0; 
  display: inline-block;
}

.contact-info {
  margin-top: 2rem; 
  font-size: 1rem;
  color: var(--subtext-color);
}
.contact-info p {
  margin: 0.5rem 0; 
}


/* ===================== FOOTER ===================== */
footer {
  background: #111111;
  color: #FFFFFF;
  padding: 20px;
  text-align: center;
}

