.service-card:hover {
  transform: translateY(-8px);
  box-shadow: 0 15px 35px rgba(0,0,0,0.15);
}

.service-card h3 {
  color: #ff6b35;
  font-size: 1.5rem;
  margin-bottom: 1rem;
}

.service-card ul {
  list-style: none;
  padding-left: 0;
  margin-bottom: 1.5rem;
}

.service-card ul li {
  position: relative;
  padding-left: 25px;
  margin-bottom: 0.8rem;
  color: #444;
  font-size: 1rem;
}

.service-card ul li::before {
  content: "✓";
  position: absolute;
  left: 0;
  color: #ff6b35;
  font-weight: bold;
}

/* Buttons */
.service-card .btn {
  display: inline-block;
  text-decoration: none;
  padding: 0.65rem 1.5rem;
  border-radius: 25px;
  background: #ff6b35;
  color: white;
  font-weight: 600;
  transition: all 0.3s ease;
}

.service-card .btn:hover {
  background: #ff7f50;
  color: white;
}

/* Optional unique card styles */
.service-card.new-web {
  border-top: 4px solid #ff6b35;
}

.service-card.upgrade-web {
  border-top: 4px solid #ff9f1c;
}


/* Services Grid */
.contact-services {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
  gap: 20px;
  margin-bottom: 3rem;
}

.service-item {
  background: #fff7f3;
  padding: 20px;
  border-radius: 12px;
  font-weight: 600;
  color: #ff6b35;
  box-shadow: 0 3px 8px rgba(0,0,0,0.05);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}

.service-item i {
  font-size: 2rem;
  margin-bottom: 0.5rem;
}

/* Hover Animation */
.service-item:hover {
  transform: translateY(-5px);
  box-shadow: 0 8px 20px rgba(0,0,0,0.1);
}

/* Stats */
.contact-stats {
  display: flex;
  justify-content: center;
  gap: 2rem;
  margin-bottom: 2.5rem;
  flex-wrap: wrap;
}

.contact-stats div {
  font-weight: 600;
  color: #ff6b35;
  font-size: 1.2rem;
}

/* Call-to-Action Button */
.btn {
  padding: 0.75rem 1.5rem;
  background: #ff6b35;
  color: #fff;
  border-radius: 25px;
  font-weight: 600;
  text-decoration: none;
  transition: all 0.3s ease;
  display: inline-block;
  margin-bottom: 2rem;
}

.btn:hover {
  transform: scale(1.05);
  box-shadow: 0 5px 20px rgba(255,107,53,0.4);
}

/* Contact Info */
.contact-info p {
  margin: 0.5rem 0;
  font-weight: 500;
  color: #333;
}


/* Footer */
footer {
  background: #111;
  color: white;
  padding: 20px;
  text-align: center;
}
